        public static string GetCVEmailNotificationContent(CV cv)
        {
            var content = new StringBuilder();
            content.AppendLine("Se ha registrado una nueva hoja de vida.");
            content.Append(Environment.NewLine);
            content.AppendLine("Los datos de la persona son los siguientes.");
            content.Append(Environment.NewLine);
            content.AppendLine($"Nombre: {cv.Names} {cv.LastNames}");
            content.AppendLine($"Género: {cv.Gender}");
            content.AppendLine($"Fecha nacimiento: {cv.BirthDate.ToString("dd/MM/yyyy")}");
            content.AppendLine($"Email: {cv.Email}");
            content.AppendLine($"Teléfono: {cv.Phone}");
            content.AppendLine($"Profesión: {cv.Profession}");
            content.Append(Environment.NewLine);

            return content.ToString();
        }

        public static string GetPQREmailNotificationContent(Pqr pqr)
        {
            var content = new StringBuilder();
            content.AppendLine("Se ha registrado una nueva PQR. La información es la siguiente:");
            content.Append(Environment.NewLine);
            content.AppendLine($"Nombre: {pqr.Names} {pqr.LastNames}");
            content.AppendLine($"Identificación: {pqr.Identification}");
            content.AppendLine($"Código usuario: {(string.IsNullOrEmpty(pqr.UserCode) ? "N/A" : pqr.UserCode)}");
            content.AppendLine($"Dirección notificación: {pqr.NotificationAddress}");
            content.AppendLine($"Dirección predio: {(string.IsNullOrEmpty(pqr.Address) ? "N/A" : pqr.Address)}");
            content.AppendLine($"Teléfono: {pqr.Phone}");
            content.AppendLine($"Email: {pqr.Email}");
            content.AppendLine($"Ciudad: {pqr.City}");
            content.AppendLine($"Barrio: {pqr.Neighborhood}");
            content.AppendLine($"Asunto: {pqr.Issue}");
            content.AppendLine($"Situación: {pqr.GetSituationDescription()}");
            content.AppendLine($"Descripción: {pqr.Description}");
            content.Append(Environment.NewLine);

            return content.ToString();
        }

